function App() {
  const [count, setCount] = useState(0);
  const countHandler = () => {
    setCount(count + 1);
  };
  return (
    <div>
      {/* <FilterReducer></FilterReducer> */}
      {/* <Counter></Counter> */}
      <B onClick={countHandler}></B>
      <MyContext.Provider value={count}>
        <C></C>
      </MyContext.Provider>
    </div>
  );
}

export default App;
